import React, { useState, useEffect } from 'react';
import bridge from '@vkontakte/vk-bridge';
import {
	View,
	AdaptivityProvider,
	AppRoot,
	ConfigProvider,
	SplitLayout,
	SplitCol,
	ModalCard,
	Button, ModalRoot
} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import Persik from './panels/Persik';
import Example from './panels/example';
import user_menu from './panels/user_menu'
import User_menu from "./panels/user_menu";
import {Icon56MoneyTransferOutline} from "@vkontakte/icons";


const Routes = {
	USER_MENU:'user_menu',
	EXAMPLE:'Example',
	PERSIK:'Persik'
}

const App = () => {
	const [activePanel, setActivePanel] = useState(Routes.USER_MENU);
	const [fetchedUser, setUser] = useState(null);
	const [popout, setPopout] = useState(null);
	const [activeModal, setActiveModal] = useState(null);
	const [modalHistory, setModalHistory] = useState([]);

	useEffect(() => {
		async function fetchData() {
			const user = await bridge.send('VKWebAppGetUserInfo');
			setUser(user);
			setPopout(null);
		}
		fetchData();
	}, []);

	const changeActiveModal = (activeModal) => {
		activeModal = activeModal || null;
		let localModalHistory = modalHistory ? [...modalHistory] : [];

		if (activeModal === null) {
			localModalHistory = [];
		} else if (modalHistory.indexOf(activeModal) !== -1) {
			localModalHistory = localModalHistory.splice(0, localModalHistory.indexOf(activeModal) + 1);
		} else {
			localModalHistory.push(activeModal);
		}

		setActiveModal(activeModal);
		setModalHistory(localModalHistory);
	};

	const go = e => {
		setActivePanel(e.currentTarget.dataset.to);
	};

	const modal = (
		<ModalRoot activeModal={activeModal} onClose={modalBack}>
			<ModalCard
				id="more_info"
				onClose={() => changeActiveModal(null)}
				icon={<Icon56MoneyTransferOutline />}
				header="Отправляйте деньги друзьям, используя банковскую карту"
				subheader="Номер карты получателя не нужен — он сам решит, куда зачислить средства."
				actions={
					<Button
						size="l"
						mode="primary"
						stretched
						onClick={() => changeActiveModal(null)}
					>
						Попробовать
					</Button>
				}
			></ModalCard>
		</ModalRoot>
	)


	return (
		<ConfigProvider>
			<AdaptivityProvider>
				<AppRoot>
					<SplitLayout popout={popout} modal={modal}>
						<SplitCol>
							<View activePanel={activePanel}>
								<User_menu id={Routes.USER_MENU} activeModal={activeModal} changeActiveModal={changeActiveModal} modalBack={modalBack} go={go} />
								<Example id={Routes.EXAMPLE} go={go} />
								<Persik id={Routes.PERSIK} go={go} />
							</View>
						</SplitCol>
					</SplitLayout>
				</AppRoot>
			</AdaptivityProvider>
		</ConfigProvider>
	);
}

export default App;
